mapper.update:
update clazz
name=#{name}, room=#{room}, begin_date=#{beginDate}, end_date=#{endDate}, master_id=#{masterId}, subject=#{subject}, create_time=#{createTime}, update_time=NOW()
where id=#{id};

mapper.select:
select clazz.id, clazz.name, room, begin_date, end_date, master_id, subject, clazz.create_time, clazz.update_time,emp.name masterName
from
    clazz left join emp on clazz.master_id=emp.id


        clazz.name like CONCAT('%',#{name},'%')


        and end_date between #{begin} and #{end}


order by clazz.update_time desc;
