mapper.update:
update student
name=#{name}, no=#{no}, gender=#{gender}, phone=#{phone}, is_college=#{isCollege}, id_card=#{idCard}, address=#{address}, degree=#{degree}, graduation_date=#{graduationDate}, clazz_id=#{clazzId}, update_time=NOW()
where id=#{id};

mapper.select:
# select student.name,student.no,clazz.name,student.clazz_id, # student.gender,student.phone,student.degree,student.violation_count,student.violation_score,student.update_time select student.*,clazz.name clazzName from clazz right join student on clazz.id=student.clazz_id
student.name like CONCAT('%',#{name},'%')
and student.degree=#{degree}
and student.clazz_id=#{clazzId}
order by student.update_time desc

select count(*)
from clazz right join student on clazz.id=student.clazz_id


        student.name like CONCAT('%',#{name},'%')


